    private void btnBuatActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnBuatActionPerformed
        // TODO add your handling code here:
        try {
    String tanggalStr = txttanggal.getText(); 
    java.sql.Date tanggalSQL = java.sql.Date.valueOf(tanggalStr); 
    
    int idPasien = session.idUser;
    int idDokter = getIdDokterFromCombo(comboDokter.getSelectedItem().toString());
    String waktu = comboWaktu.getSelectedItem().toString();
    String keluhan = txtKeluhan.getText();
    String status = "MENUNGGU";

    String sql = "INSERT INTO jadwal_konsultasi (id_pasien, id_dokter, tanggal, waktu, keluhan, status) VALUES (?, ?, ?, ?, ?, ?)";
    PreparedStatement pst = conn.prepareStatement(sql);
    pst.setInt(1, idPasien);
    pst.setInt(2, idDokter);
    pst.setDate(3, tanggalSQL);
    pst.setString(4, waktu);
    pst.setString(5, keluhan);
    pst.setString(6, status);

    pst.executeUpdate();
    JOptionPane.showMessageDialog(this, "Jadwal berhasil dibuat!");
} catch (IllegalArgumentException e) {
    JOptionPane.showMessageDialog(this, "Format tanggal salah! Gunakan YYYY-MM-DD");
} catch (SQLException ex) {
    JOptionPane.showMessageDialog(this, "Error: " + ex.getMessage());
}
    }//GEN-LAST:event_btnBuatActionPerformed

