private void loadDokter() {
    try {
        String sql = "SELECT id_user, nama_lengkap FROM user WHERE role = 'dokter'";
        PreparedStatement pst = conn.prepareStatement(sql);
        ResultSet rs = pst.executeQuery();
        comboDokter.removeAllItems();
        while(rs.next()) {
            // simpan id dokter sebagai item tersembunyi (opsi sederhana: "id-nama")
            comboDokter.addItem(rs.getInt("id_user") + " - " + rs.getString("nama_lengkap"));
        }
    } catch(SQLException e) {
        JOptionPane.showMessageDialog(this, "Error load dokter: " + e.getMessage());
    }
}
private int getIdDokterFromCombo(String comboItem) throws SQLException {
         String[] parts = comboItem.split(" - ");
        return Integer.parseInt(parts[0]); // ambil id_user
    }
